                var payload = mc.getPayloadJSON();

                // Request body variables
                var direct_message_deep_link  = mc.getProperty('uri.var.direct_message_deep_link');
                var for_super_followers_only  = mc.getProperty('uri.var.for_super_followers_only');
                var geo  = mc.getProperty('uri.var.geo');
                var media  = mc.getProperty('uri.var.media');
                var poll  = mc.getProperty('uri.var.poll');
                var quote_tweet_id  = mc.getProperty('uri.var.quote_tweet_id');
                var reply  = mc.getProperty('uri.var.reply');
                var reply_settings  = mc.getProperty('uri.var.reply_settings');
                var text  = mc.getProperty('uri.var.text');

                payload.for_super_followers_only = false;

                // Add variables to the request body
                if (direct_message_deep_link != null && direct_message_deep_link != "") {
                    payload.direct_message_deep_link = direct_message_deep_link;
                } if (for_super_followers_only != null && for_super_followers_only == true) {
                    payload.for_super_followers_only = true;
                } if (geo != null && geo != "") {
                    payload.geo = JSON.parse(geo);
                } if (media != null && media != "") {
                    payload.geo = JSON.parse(geo);
                } if (poll != null && poll != "") {
                    payload.poll = JSON.parse(poll);
				} if (quote_tweet_id != null && quote_tweet_id !=  "") {
                    payload.quote_tweet_id = quote_tweet_id;
                } if (reply != null && reply != "") {
                    payload.geo = JSON.parse(geo);
                } if (reply_settings != null && reply_settings != "") {
                    payload.reply_settings = reply_settings;
                } if (text != null && text != "") {
                    payload.text = text;
                }

                mc.setPayloadJSON(payload);
